import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import LineChart from 'react-google-charts'

const Views = () => {
    const [integers, setIntegers] = useState([])
    const [count, setCount] = useState(1);
    const [chartData, setChartData] = useState([])

    async function fetchAllInts(){
        try {
            let response = await axios.get("http://localhost:8800/data/" + count);
            console.log(count);
            setIntegers(response.data);

            if (count === 50){
                setCount(1);
            } else {
                setCount(count + 1);
            }

        } catch(err){
            console.log(err)
        }
    }

    function createData(){
        const data = [['Y-Axis', 'dBM']]
        let currentMHZ = 850;
        for (let i = 0; i < integers.length; i++){
            data.push([currentMHZ, integers[i]])
            currentMHZ += (1 / 2)
        }

        return data;
    }

    useEffect(() => {   
        const interval = setInterval(() => {
            fetchAllInts();
            setChartData(createData());
        }, 1000);

        return () => clearInterval(interval);
    })

    const options = {
        title: 'LP Technologies Graph',
        curveType: 'function',
        legend: {position: 'bottom'}
    }


    return (
        <div>
            <LineChart
                chartType = "LineChart"
                data = {chartData}
                options = {options}
                width="100%"
                height="400px"
                />
        </div>
    )
}

export default Views;
