import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import Chart from 'chart.js'

var count = 1
const Views = () => {
    const [integers, setIntegers] = useState([])

    async function fetchAllInts(counts){
        try {
            let response = await axios.get("http://localhost:8800/data/" + counts);
            console.log(response);
            setIntegers(response.data);
            if (counts === 50){
                counts = 1;
            }
        } catch(err){
            console.log(err)
        }
    }

    useEffect(() => {   
        fetchAllInts(count)
    }, [])


    return (
        <div>
            <h1>Test Data</h1>
            <div className="data">
                <button onClick={() => count = count + 1}>+</button>
                <p>Value of count{count}</p>
            </div>
        </div>
    )
}

export default Views;
