import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import LineChart from 'react-google-charts'

const Views = () => {
    const [integers, setIntegers] = useState([]) //Will hold the data points from the SQL Database
    const [count, setCount] = useState(1)
    const [chartData, setChartData] = useState([]) //Handles graph updates
    const [date, setDate] = useState([]);


    const fetchdata = async () => {
        const integerAPI = "http://localhost:8800/data/" + count;
        const dateAPI = "http://localhost:8800/date/" + count;

        const getInteger = await axios.get(integerAPI);
        const getDate = await axios.get(dateAPI);

        console.log(getInteger);
        setIntegers(getInteger.data);

        axios.all([getInteger, getDate]).then(
            axios.spread((...allData) => {
                setIntegers(allData[0].data);
                setDate(allData[1].data);

                count += 1;
                console.log(integers);
                console.log(date);

                function createData(){ //This formats the data for our chart to use
                    const data = [['MHZ', 'dBM']]
                    let startingMHZ = 850;
            
                    for (let i = 0; i < integers.length; i++){
                        data.push([startingMHZ, integers[i]])
                        startingMHZ += (0.5)
                    }
            
                    return data;
                }

                setChartData(createData());

            })
        )
    }

    useEffect(() => { //Handles the updates of our variables, changing every 1 second
        fetchdata();
    }, [])

    const options = { //Chart Options that can be changed as needed.
        title: 'LP Technologies Graph',
        curveType: 'function',
        legend: {position: 'bottom'}
    }


    return (
        <div>
            <h1>{count}</h1>
            <h3>{date.trace_time}</h3>
            {chartData.length > 0 && (
            <LineChart
                chartType = "LineChart"
                data = {chartData}
                options = {options}
                width="100%"
                height="400px"
                />
            )}

        </div>
    )
}

export default Views;
