import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import LineChart from 'react-google-charts'

const Views = () => {
    const [integers, setIntegers] = useState([]) //Will hold the data points from the SQL Database
    const [count, setCount] = useState(1); //Keeps track of list of data we're on
    const [chartData, setChartData] = useState([]) //Handles graph updates
    const [date, setDate] = useState([]);


    const fetchdata = () => {
        const integerAPI = "http://localhost:8800/data/" + count;
        const dateAPI = "http://localhost:8800/date/" + count;

        const getInteger = axios.get(integerAPI);
        const getDate = axios.get(dateAPI);
        axios.all([getInteger, getDate]).then(
            axios.spread((...allData) => {
                console.log(allData[0].data);
                console.log(allData[1].data);
            })
        )
    }



    async function fetchAllInts(){
        try {
            //Grab API Response, store it in our needed variables, and make sure count never goes out of bound
            let response = await axios.get("http://localhost:8800/data/" + count);
            setIntegers(response.data);
        } catch(err){
            console.log(err)
        }
    }

    async function fetchDate(){
        try {
            //Grab API Response, store it in our needed variables, and make sure count never goes out of bound
            let response = await axios.get("http://localhost:8800/date/" + count);
            setDate(response.data);
        } catch(err){
            console.log(err)
        }
    }

    function createData(){ //This formats the data for our chart to use
        const data = [['MHZ', 'dBM']]
        let startingMHZ = 850;

        for (let i = 0; i < integers.length; i++){
            data.push([startingMHZ, integers[i]])
            startingMHZ += (0.5)
        }

        return data;
    }

    useEffect(() => { //Handles the updates of our variables, changing every 1 second
        const interval = setInterval(() => {
            fetchAllInts();
            fetchDate();
            setChartData(createData());
            if (count === 50){
                setCount(1);
            } else {
                setCount(count + 1);
            }
        }, 4000);

        
        return () => clearInterval(interval);
    })

    const options = { //Chart Options that can be changed as needed.
        title: 'LP Technologies Graph',
        curveType: 'function',
        legend: {position: 'bottom'}
    }


    return (
        <div>
            <h1>{count}</h1>
            <h3>{date.trace_time}</h3>
            {chartData.length > 0 && (
            <LineChart
                chartType = "LineChart"
                data = {chartData}
                options = {options}
                width="100%"
                height="400px"
                />
            )}

        </div>
    )
}

export default Views;
