import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import LineChart from 'react-google-charts'

const Views = () => {
    const [integers, setIntegers] = useState([]) //Will hold the data points from the SQL Database
    const [count, setCount] = useState(49)
    const [dateCount, setDateCount] = useState(46); //Debugging - API calls are behind despite async set up
    const [chartData, setChartData] = useState([]) //Handles graph updates
    const [date, setDate] = useState([]);

    useEffect(() => { //Handles the updates of our variables, changing every 1 second
        const fetchdata = async () => {
            const integerAPI = "http://localhost:8800/data/" + count;
            const dateAPI = "http://localhost:8800/date/" + dateCount;
    
            const getInteger = await axios.get(integerAPI);
            const getDate = await axios.get(dateAPI);
    
            axios.all([getInteger, getDate]).then(
                axios.spread((...allData) => {
                    setIntegers(allData[0].data);
                    setDate(allData[1].data);

                    const data = [['MHZ', 'dBM']]
                    let startingMHZ = 850;

                    for (let i = 0; i < integers.length; i++){
                        data.push([startingMHZ, integers[i]])
                        startingMHZ += (0.5)
                    }

                    setChartData(data);
                    if (count === 50){
                        setCount(1)
                    } else {
                    setCount(count + 1);
                    }

                    if (dateCount === 50){
                        setDateCount(1)
                    } else {
                    setDateCount(count + 1);
                    }

                })
            )
        }

        setTimeout(() => {
            fetchdata();
        }, 3500)
    })

    const test = date.trace_time;

    const options = { //Chart Options that can be changed as needed.
        title: 'LP Technologies Graph',
        curveType: 'function',
        legend: {position: 'bottom'}
    }
    return (
        <div>
            
            <h2 className="time">{test}</h2>
            <h1>{count - 1}</h1>
            <h1>{dateCount - 1}</h1>
            {chartData.length > 1 && (
            <LineChart
                chartType = "LineChart"
                data = {chartData}
                options = {options}
                width="100%"
                height="400px"
                />
            )}

        </div>
    )
}

export default Views;
